def page_template(*components, page_title: str = None) -> list:
    def page_title_component() -> list:
        return [c.Heading(
            text=page_title,
            class_name='flex-1 lg:flex-none font-semibold text-base-content',
        )] if page_title else []

    return [
        c.AppShell(
            hasDrawer=True,
            components=[
                *app_drawer(),
                c.AppContent(
                    components=[
                        c.PageHeader(
                            components=[
                                *page_title_component(),
                                *toolbar_menu()
                            ]
                        ),
                        c.PageContent(
                            components=[
                                *components,
                            ]
                        ),
                        # *app_footer(),
                    ],
                ),
            ],
        )
    ]
